jQuery(document).ready(function (a) {
  action()
});
function action() {
  $(document.body).append(
      '<div id="roll" style="display:none;"><div title="回到顶部" id="roll_top"></div></div>');
  $("#roll_top").click(function () {
    $("html,body").animate({scrollTop: "0px"}, 800)
  });
  $("#ct").click(function () {
    $("html,body").animate({scrollTop: $(".ct").offset().top}, 800)
  });
  $(window).scroll(function (a) {
    if ($.browser.ie6) {
      $("#roll").css("top", $(this).scrollTop() + $(this).height() - 170)
    }
    if ($(this).scrollTop() > 260) {
      $("#roll").fadeIn()
    } else {
      $("#roll").fadeOut()
    }
  })
};

/*在线客服开始*/
function imState() {
  $.get("/ajax.ashx?action=imstate&t=" + Math.random(), function (rsp) {
    window.imInfo = eval("(" + rsp + ")");
    if (imInfo.imShow) {
      $.getScript("/UserControls/Im/im.js?t=" + Math.random(), function () {
        writeIm(imInfo.imData);
      });
    } else {
      $("#divOranIm").hide();
    }
  });
}

$(function () {
  $("body").append(
      "<div style='width:150px;z-index:10;' id='divOranIm'></div>");
  imState();
});
/*在线客服结束*/

